Authorization = "HP" + " " + ProjectID + ":" + AccessKeyId + ":" + Signature;

Signature = Base64( HMAC-SHA1( SecretKey, UTF-8-Encoding-Of( StringToSign ) ) );

StringToSign = HTTP-Verb + "\n" +
    Content-MD5 + "\n" +
    Content-Type + "\n" +
    Date + "\n" +
    CanonicalizedResource;

CanonicalizedResource = (described below)

